var asm = require('../asm');
    
asm.serve.startListening = function(port, app, token) {
    var usePort = port || 3000;
    
    console.log("[serve.start] Using token: ".green + token.green);
    console.log("[serve.start] Listening on port: ".green + usePort.toString().green);
    app.listen(usePort);
};